### `components/PaymentButton.jsx`
```jsx
import { loadStripe } from "@stripe/stripe-js";
import {
  Elements,
  useStripe,
  useElements,
  PaymentElement,
} from "@stripe/react-stripe-js";
import { useState } from "react";

const stripePromise = loadStripe("pk_test_XXXXXXXXXXXXXXXXXXXXXXXX");

export default function PaymentButton({ amount }) {
  const [clientSecret, setClientSecret] = useState("");

  const createPaymentIntent = async () => {
    const res = await fetch("/api/create-payment-intent", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ amount }),
    });
    const data = await res.json();
    setClientSecret(data.clientSecret);
  };

  const appearance = {};
  const options = {
    clientSecret,
    appearance,
  };

  if (!clientSecret) {
    return (
      <button onClick={createPaymentIntent} className="bg-green-600 text-white px-4 py-2 rounded">
        Pay Now
      </button>
    );
  }

  return (
    <Elements stripe={stripePromise} options={options}>
      <CheckoutForm />
    </Elements>
  );
}

function CheckoutForm() {
  const stripe = useStripe();
  const elements = useElements();
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!stripe || !elements) return;

    const result = await stripe.confirmPayment({
      elements,
      confirmParams: {
        return_url: `${window.location.origin}/payment-success`,
      },
    });

    if (result.error) {
      setMessage(result.error.message);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <PaymentElement />
      <button type="submit" disabled={!stripe} className="mt-4 w-full bg-indigo-600 text-white py-2 rounded">
        Confirm Payment
      </button>
      {message && <p className="text-red-500 mt-2">{message}</p>}
    </form>
  );
}
```

